INSERT INTO `chemical` VALUES ('161', '十二烷基苯磺酸钠', '地下室', '1003001001B001021102', '2-1-1-2', '莫成昊', '否', '是', '1', '35.00', 'C18H29NaO3S', '348.48', '25155-30-0', '', '', '白色或浅黄色粉末或颗粒。溶于水、热乙醇。其水溶液极易起泡，但粘度较低，且易消失，有较好的渗透力和去污力。有毒，半数致死量(小鼠，经口)2g/kg。有刺激性。');
INSERT INTO `chemical` VALUES ('162', '氧化锌', '地下室', '1003001001B001021103', '2-1-1-3', '莫成昊', '否', '是', '1', '38.00', 'ZnO', '81.39', '1314-13-2', '', '', '性状：白色、浅黄色粉末或六方结晶。无气味。味苦。在正常压力下能升华。能吸收空气中的二氧化碳。加热至300℃色变黄，但冷却后又成白色。溶于稀乙酸、矿酸、氨水、碳酸铵和氢氧化碱溶液，几乎不溶于水。相对密度5.67(六方结晶)，(d204)5.607。熔点1800℃以上。折光率(nD)2.00');
INSERT INTO `chemical` VALUES ('163', '氧化钙', '地下室', '1003001001B001021104', '2-1-1-4', '莫成昊', '否', '是', '1', '18.00', 'CaO', '56.08', '1305-78-8', '', '', '白色或带灰色块状或颗粒。对湿敏感。易从空气中吸收二氧化碳及水分。溶于水成氢氧化钙并产生大量热，溶于酸类、甘油和蔗糖溶液，几乎不溶于乙醇。相对密度3.32～3.35。熔点2572℃。沸点2850℃。折光率1.838。有腐蚀性。');
