INSERT INTO `chemical` VALUES ('166', '钨酸钠', '地下室', '1003001001B001021107', '2-1-1-7', '莫成昊', '否', '是', '1', '119.00', 'Na2WO42H2O', '329.85', '10213-10-2', '', '', '无色结晶或白色结晶性粉末。在干燥空气中风化，100℃时失去结晶水。溶于1.1份水，不溶于乙醇。水溶液呈弱碱性，pH为8～9。相对密度3.23～3.25。熔点698℃(无水品)。有毒，致死量(豚鼠，经口)990mg/kg。有刺激性。');
INSERT INTO `chemical` VALUES ('167', '磷酸钠', '地下室', '1003001001B001021108', '2-1-1-8', '莫成昊', '否', '是', '1', '15.00', '(NaPO3)6', '611.77', '10124-56-8', '', '', '无色透明玻璃片状或微粒结晶。为聚偏磷酸盐的混合物，非六聚体。有吸湿性。缓慢溶于水，水溶液中发生解聚作用，形成三偏磷酸钠和正磷酸钠。熔点628℃。');
INSERT INTO `chemical` VALUES ('168', '七水合硫酸亚铁', '地下室', '1003001001B001021109', '2-1-1-9', '莫成昊', '否', '是', '1', '15.00', 'FeSO47H2O', '278.01', '7782-63-0', '', '', '性状：蓝绿色单斜结晶或颗粒。无气味。在干燥空气中风化。在潮湿空气中表面氧化成棕色的碱式硫酸高铁。在56.6℃成为四水合物,在65℃时成为一水合物。溶于水,几乎不溶于乙醇。其水溶液冷时在空气中缓慢氧化,在热时较快氧化。加入碱或露光能加速其氧化。相对密度(d15)1.897。半数致死');
INSERT INTO `chemical` VALUES ('169', '硼酸', '地下室', '1003001001B001021110', '2-1-1-10', '莫成昊', '否', '是', '1', '25.00', 'H3BO3', '61.83', '10043-35-3', '', '', '白色结晶性粉末或无色微带珍珠状光泽的鳞片。无气味。味微酸苦后带甜。与皮肤接触有滑腻感。露置空气中无变化。能随水蒸气挥发。加热至100～105℃时失去一分子水而形成偏硼酸,于104～160℃时长时间加热转变为焦硼酸,更高温度则形成无水物。0.1mol/L水溶液pH为5.1。1G能溶于1');
INSERT INTO `chemical` VALUES ('170', '尿素', '地下室', '1003001001B001021201', '2-1-2-1', '莫成昊', '否', '是', '1', '16.00', 'CH4N2O', '60.06', '57-13-6', '', '', '白色四方棱柱状结晶或粉末。有氨的气味和咸味。加热温度高于其熔点时则分解成缩二脲、氨和三聚氰酸。1g溶于1ml水、10ml95％乙醇、1ml95％沸乙醇、20ml无水乙醇、6ml甲醇和2ml甘油。溶于浓盐酸，几乎不溶于乙醚和氯仿。10％水溶液的pH为7.2。水溶液相对密度(W/W，d184');
INSERT INTO `chemical` VALUES ('171', '无水碳酸钠', '地下室', '1003001001B001021202', '2-1-2-2', '莫成昊', '否', '是', '1', '14.00', 'Na2CO3', '105.99', '497-19-8', '', '', '白色粉末或颗粒。无气味。有碱味。有吸湿性。露置空气中逐渐吸收1molL水分(约15%)。400℃时开始失去二氧化碳。遇酸分解并泡腾。溶于水(室温时3.5份,35℃时2.2份)和甘油,不溶于乙醇。水溶液呈强碱性,pH11.6。相对密度2.53。熔点851℃。半数致死量(30日)(小鼠,腹腔)116.6mg');
