INSERT INTO `chemical` VALUES ('175', '偏钒酸铵', '地下室', '1003001001B001021206', '2-1-2-6', '莫成昊', '是', '是', '1', '71.00', 'NH4VO3', '116.98', '7803-55-6', '', '', '性状：白色或微黄色结晶性粉末。有吸湿性。溶于热水和稀氨水,微溶于冷水,不溶于乙醇。加热失去氨和水。相对密度2.326。熔点200℃(分解)。中等毒,半数致死量(大鼠,经口)160mG/kG。有刺激性。');
INSERT INTO `chemical` VALUES ('176', '柠檬酸钠', '地下室', '1003001001B001021207', '2-1-2-7', '莫成昊', '否', '是', '1', '12.00', 'C6H5Na3O72H2O', '294.1', '6132/4/3', '', '', '性状：白色结晶颗粒或粉末。无气味。味咸而凉。空气中稳定。在150℃时失水。溶于水(1.3份冷水、0.6份沸水),不溶于乙醇。水溶液对石蕊呈微碱性,pH约为8。相对密度(d23.5４)1.857。半数致死量(大鼠,腹腔)1548mg/kg。');
INSERT INTO `chemical` VALUES ('177', '十二水合硫酸铬钾', '地下室', '1003001001B001021208', '2-1-2-8', '莫成昊', '否', '是', '1', '20.00', 'CrK(SO4)212H2O', '499.4', '7788-99-0', '', '', '紫红色到黑色八面立方结晶。在透射光下呈宝石红色。在空气中风化。100℃时失去10分子结晶水，400℃失去全部结晶水。溶于4份冷水、2份沸水，几乎不溶于乙醇。其水溶液冷时呈紫色，热时呈绿色。相对密度(d25)1.83。熔点89℃。半数致死量(大鼠，静脉)112mg/kg。有刺激性。');
INSERT INTO `chemical` VALUES ('178', '邻苯二甲酸酐', '地下室', '1003001001B001021209', '2-1-2-9', '莫成昊', '是', '是', '1', '12.00', 'C8H4O3', '148.12', '85-44-9', '', '', '白色有光泽针状结晶。能升华。易溶于热水，成为苯二甲酸，溶于乙醇，微溶于冷水、二硫化碳和乙醚。相对密度1.53。熔点130.8℃。沸点295℃。闪点152℃。有刺激性。');
INSERT INTO `chemical` VALUES ('179', '蔗糖', '地下室', '1003001001B001021210', '2-1-2-10', '莫成昊', '否', '是', '1', '26.00', 'C12H22O11', '342.3', '57-50-1', '', '', '无色单斜楔形结晶或白色颗粒或结晶性粉末。味甜。有吸湿性。在空气中稳定。不能还原斐林氏溶液。不形成脎。无变旋光性。在160～186℃分解。能被稀酸转化酶水解成葡萄糖和果糖。1g溶于0.5ml水、170ml乙醇、约100ml甲醇，微溶于甘油、吡啶。相对密度(d254)1.587。熔');
INSERT INTO `chemical` VALUES ('180', '硅酸钠', '地下室', '1003001001B001021211', '2-1-2-11', '莫成昊', '否', '是', '1', '23.00', 'Na2SiO39H2O', '284.2', '13517-24-3', '', '', '无色正交双锥结晶或白色至灰白色块状物或粉末。能风化。在100℃时失去6分子结晶水。易溶于水，溶于稀氢氧化钠溶液，不溶于乙醇和酸。熔点40～48℃。低毒，半数致死量(大鼠，经口)1280mg/kg(无结晶水)');
INSERT INTO `chemical` VALUES ('181', '轻质氧化镁', '地下室', '1003001001B001021301', '2-1-3-1', '莫成昊', '否', '是', '3', '86.00', '', '', '', '', '', '');
INSERT INTO `chemical` VALUES ('182', '硅胶', '地下室', '1003001001B001021304', '2-1-3-4', '莫成昊', '否', '是', '1', '37.00', 'mSiO2xH2O', 'NA', '112926-00-8', '', '', '玻璃状微带棕色无定形多孔性颗粒或小珠状。为部分水合的二氧化硅。有吸湿性。耐热、耐盐酸、硫酸、硝酸等的浸渍。具高活性,可再生。溶于氢氟酸和强碱溶液,不溶于水。');
