INSERT INTO `chemical` VALUES ('185', '三氧化铬', '地下室', '1003001001B001021308', '2-1-3-8', '莫成昊', '是', '是', '1', '51.00', 'CrO3', '99.99', '1333-82-0', '', '', '深红色双锥体棱晶、片状结晶或颗粒。易吸湿。在250℃分解成三氧化二铬和氧。易溶于水，溶于硫酸。有氧化性，能氧化乙醇和其他有机物质而引起燃烧。相对密度2.70。熔点197℃。有毒，最小致死量(狗，皮下)330mg/kg。有致癌可能性。');
INSERT INTO `chemical` VALUES ('186', '无机粘结剂：A氯化铜', '地下室', '1003001001B001021309', '2-1-3-9', '莫成昊', '否', '是', '1', null, '', '', '', '', '', '');
