INSERT INTO `chemical` VALUES ('188', 'N-N\'亚甲基双丙烯酰胺', '地下室', '1003001001B001021402', '2-1-4-2', '莫成昊', '否', '是', '1', '28.00', 'C7H10N2O2', '154.17', '110-26-9', '', '', '白色粉末状结晶。无气味。对光敏感。20℃时水中可溶0.31G/ml,水溶液可因水解而形成丙烯酸和氨。熔点>300℃。中等毒,半数致死量(大鼠,经口)390mG/kG。有刺激性。');
INSERT INTO `chemical` VALUES ('189', '石油醚', '地下室', '1003001001B001022101', '2-2-1-1', '莫成昊', '是', '是', '2', '121.00', 'NA', 'NA', '8032-32-4', '', '', '无色透明液体。有特殊气味。无荧光。易挥发。低粘度。在冷时不凝固。能与无水乙醇、苯、氯仿、乙醚、二硫化碳、四氯化碳和油类(蓖麻油除外)混溶，不溶于水。相对密度0.625～0.660。沸点30～120℃。易燃。其蒸气与空气混合灼烧能引起爆炸。');
INSERT INTO `chemical` VALUES ('190', '四氯化碳', '地下室', '1003001001B001022103', '2-2-1-3', '莫成昊', '是', '是', '1', '70.00', 'CCl4', '153.82', '56-23-5', '', '', '无色澄清重质不燃液体。有特殊气味。蒸气比空气重。有湿气存在时逐渐分解成有剧毒的光气和氯化氢。能与乙醇、乙醚、二硫化碳、石油醚、氯仿、苯和油等混溶，微溶于水(1∶2000)。相对密度(d2525)1.589。凝固点-23℃。沸点76.7℃。折光率(n２０Ｄ)1.4607。低毒，半数致死量(大鼠，经口)2920mg/kg。经试验表明，对实验动物有潜在致癌作用。');
INSERT INTO `chemical` VALUES ('191', '无水乙醚', '地下室', '1003001001B001022104', '2-2-1-4', '莫成昊', '是', '是', '1', '18.50', 'C4H10O', '74.12', '60-29-7', '', '', '同乙醚，但水分含量较少。剧烈振摇时可能产生静电而起火。');
INSERT INTO `chemical` VALUES ('192', '甲醇', '地下室', '1003001001B001022105', '2-2-1-5', '莫成昊', '是', '是', '3', '11.00', 'CH4O', '32.04', '67-56-1', '', '', '无色澄清液体。微有乙醇样气味。易挥发。易流动。燃烧时无烟有蓝色火焰。能与多种化合物形成共沸混合物。能与水、乙醇、乙醚、苯、酮类和其他有机溶剂混溶。溶解性能优于乙醇,能溶解多种无机盐类,如碘化钠、氯化钙、硝酸铵、硫酸铜、硝酸银、氯化铵和氯化钠等。相对密度(d204');
INSERT INTO `chemical` VALUES ('193', '甲醛', '地下室', '1003001001B001022108', '2-2-1-8', '莫成昊', '是', '是', '3', '108.00', 'CH2O', '30.03', '50-00-0', '', '', '无色液体。本品为约含37%甲醛(HCHO)气体的水溶液(重量计),一般含10%～15%的甲醇,以防止其聚合。有刺激性气味。在较冷时久贮易混浊,在低温时则形成三聚甲醛沉淀。蒸发时有一部分甲醛逸出,但多数变成三聚甲醛。本品为强还原剂,在微量碱性时还原性更强。在空气中能缓慢氧化成甲');
INSERT INTO `chemical` VALUES ('194', '二氯甲烷', '地下室', '1003001001B001022111', '2-2-1-11', '莫成昊', '是', '是', '1', '20.00', 'CH2Cl2', '84.93', '1975/9/2', '', '', '无色液体。有醚样气味。易挥发。其气体不燃烧，与空气混合也不爆炸。能与乙醇、乙醚和N,N捕甲基甲酰胺混溶，溶于约50份水中。相对密度(d204)1.3255。凝固点-95℃。沸点39.75℃。折光率(n20D)1.4244。低毒，半数致死量(大鼠，经口)2524mg/kg。高浓度蒸气有麻醉性。有刺激性');
INSERT INTO `chemical` VALUES ('195', '三氯甲烷', '地下室', '1003001001B001022201', '2-2-2-1', '莫成昊', '是', '是', '1', '35.00', 'CHCl3', '119.38', '67-66-3', '', '', '无色透明液体。有特殊气味。味甜。高折光，不燃，质重，易挥发。纯品对光敏感，遇光照或与空气中的氧作用，逐渐分解而生成剧毒的光气(碳酰氯)和氯化氢。可加入0.6%～1％的乙醇作稳定剂。能与乙醇、苯、乙醚、石油醚、四氯化碳、二硫化碳和油类等混溶、25℃时1ml溶于200ml水');
INSERT INTO `chemical` VALUES ('196', '乙腈', '地下室', '1003001001B001022202', '2-2-2-2', '莫成昊', '否', '是', '1', '42.00', '', '', '', '', '', '');
INSERT INTO `chemical` VALUES ('197', '异丙醇', '地下室', '1003001001B001022203', '2-2-2-3', '莫成昊', '否', '是', '1', '21.00', 'C3H8O', '60.1', '67-63-0', '', '', '无色透明液体。有类似乙醇、丙酮混合的气味。味微苦。能与水、乙醇、乙醚和氯仿混溶,不溶于盐溶液。能与水形成共沸混合物(含水12.3%),共沸点80.37℃。易生成过氧化物。相对密度(d204)0.78505。熔点-88.5℃。凝固点89.5℃。沸点82.5℃。折光率(n20D)1.37723。闪点(闭杯)12℃。');
INSERT INTO `chemical` VALUES ('198', '甲苯', '地下室', '1003001001B001022204', '2-2-2-4', '莫成昊', '是', '是', '1', '18.50', 'C7H8', '92.14', '108-88-3', '', '', '无色澄清液体。有苯样气味。有强折光性。能与乙醇、乙醚、丙酮、氯仿、二硫化碳和冰乙酸混溶，极微溶于水。相对密度(d204)0.866。凝固点-95℃。沸点110.6℃。折光率(n20D)1.4967。闪点(闭杯)4.4℃。易燃。蒸气能与空气形成爆炸性混合物，爆炸极限1.2%～7.0％(体积)。低毒，半数致死量(大鼠，经口)5000mg/kg。高浓度气体有麻醉性。有刺激性。');
INSERT INTO `chemical` VALUES ('199', '正戊酸', '地下室', '1003001001B001022205', '2-2-2-5', '莫成昊', '否', '是', '1', '101.00', 'C5H10O2', '102.13', '109-52-4', '', '', '无色液体。有不愉快气味。溶于30份水，易溶于乙醇和乙醚。相对密度(d204)0.939。熔点-34.5℃。沸点186～187℃。折光率(n20D)1.4086。闪点88℃。有毒，半数致死量(小鼠，静脉)1290±53mg/kg。有腐蚀性。');
INSERT INTO `chemical` VALUES ('200', '碱性螯合剂', '地下室', '1003001001B001022206', '2-2-2-6', '莫成昊', '否', '是', '1', null, '', '', '', '', '', '');
INSERT INTO `chemical` VALUES ('201', '过氧化氢', '地下室', '1003001001B001022207', '2-2-2-7', '莫成昊', '是', '是', '1', '12.80', 'H2O2', '34.01', '7722-84-1', '', '', '无色透明液体，味苦，能缓慢分解成氧和水，少量不纯物质存在时剧烈分解，碱性及金属细粉能加速其分解，亦能被多数有机溶剂分解，可加入少量N-乙酰苯胺和N-乙酰乙氧基苯胺作稳定剂。能与水混溶，溶于乙醚，不溶于石油醚。水溶液呈弱酸性。有氧化性和腐蚀性，对皮肤有化学浸蚀性，能与水混合。密度：1.11熔点：-0.43℃沸点：152℃折光率：1.3350');
