INSERT INTO `chemical` VALUES ('203', '次氯酸钠', '地下室', '1003001001B001022209', '2-2-2-9', '莫成昊', '是', '是', '1', '20.00', 'NaClO', '74.44', '7681-52-9', '', '', '黄色澄明液体。有次氯酸的气味。有强碱性。受热(35℃以上)或遇酸即分解。每100ml含有5.68g活性氯、7.8g氢氧化钠和32g碳酸钠。有氧化性和腐蚀性。');
