INSERT INTO `chemical` VALUES ('1', '丙三醇', '', '1', '', '1003001001B2040131101', '1-3-1-1-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '18.00', 'C3H8O3', '92.09', '56-81-5', '', '无色澄明黏稠液体。无臭。有暖甜味。能从空气中吸收潮气,也能吸收硫化氢、氰化氢和二氧化硫。对石蕊呈中性。长期放在0℃的低温处,能形成熔点为17.8℃有光泽的斜方晶体。遇强氧化剂如三氧化铬、氯酸钾、高锰酸钾能引起燃烧和爆炸。能与水、乙醇任意混溶,1份本品能溶于11份乙酸', '密封阴凉干燥保存。SCRC100106400258', '气相色谱固定液(最高使用温度75℃,溶剂为甲醇),分离分析低沸点含氧化合物、胺类化合物、氮或氧杂环化合物,能完全分离3-甲基吡啶(沸点144.14℃)和4-甲基吡啶(沸点145.36℃)。适用于水溶液的分析。溶剂。气量计及水压机缓震', '2018-06-09');
INSERT INTO `chemical` VALUES ('2', '二乙醇胺', '', '1', '', '1003001001B2040131102', '1-3-1-1-2', '莫成昊', '2018-04-13', '否', '试剂', '否', '40.00', 'C4H11NO2', '105.14', '111-42-2', '', '无色棱柱状结晶或微黄色粘稠液体。微有氨的气味。在潮湿空气中能强烈发烟。强碱性。能吸收空气中的二氧化碳。能与水、甲醇和丙酮混溶，微溶于苯、乙醚，几乎不溶于四氯化碳、正庚烷。0.1mol/L水溶液的pH为11。相对密度(d304)1.0881。熔点28℃。沸点268.8℃。折光率(n30D)1.4', '密封阴凉干燥保存。SCRC400141800488', '酸性气体吸收剂。乳化剂。有机合成。软化及润滑剂。气相色谱固定液(最高使用温度60℃，溶剂为甲醇)，选择性保留和分离醇、二醇、胺、吡啶、喹啉、哌嗪、硫醇、硫醚和水。', '2018-04-13');
INSERT INTO `chemical` VALUES ('3', '钛酸四正丁酯', '', '1', '', '1003001001B2040131103', '1-3-1-1-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '61.00', 'C16H36O4Ti', '340.36', '5593-70-4', '', '无色至浅黄色液体，在-55℃时为玻璃状固体。对湿敏感。受热易聚合。遇水分解。溶于除酮类外大部分有机溶剂。相对密度0.996。沸点310～314℃。折光率1.486。闪点76.6℃。中等毒，半数致死量(大鼠，经口)180mg/kg。有刺激性。', '密封干燥保存。SCRC801228', '酯交换反应。增强涂料抗热性(可热至500℃)。改进涂料、橡胶及塑料对金属表面的粘附力。缩合催化剂。交联剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('4', '丙酮', '', '1', '', '1003001001B2040131104', '1-3-1-1-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '16.00', 'C3H6O', '58.08', '67-64-1', '', '无色液体。有特殊气味。具辛辣甜味。易挥发。能与水、乙醇、N,N-二甲基甲酰胺、氯仿、乙醚及大多数油类混溶。相对密度(d2525)0.788。熔点-94℃。沸点56.5℃。折光率(n20D)1.3591。闪点-20℃。易燃。半数致死量(大鼠,经口)10.7ml/kG。有刺激性。', '密封阴凉保存。SCRC100004800004', '常用有机溶剂,具有选择性地溶解某些有机物。水溶液中有丙酮存在时,可抑制溶液中物质离解。可用于带有硫氰根的钴及铁之比色测定。显色反应检验钡和锶。显微分析用作固定剂,组织的硬化及脱水。动植物中各种成分的抽提。', '2018-04-13');
INSERT INTO `chemical` VALUES ('5', '二甲胺盐酸盐', '', '1', '', '1003001001B2040131105', '1-3-1-1-5', '莫成昊', '2018-04-13', '否', '试剂', '否', '32.00', 'C2H7NHCl', '81.54', '506-59-2', '', '白色叶状晶体。有挥发性。易潮解。易溶于水，溶于乙醇和氯仿，几乎不溶于乙醚。熔点171℃。有刺激性。', '密封干燥保存。SCRC300670', '有机合成。乙酰化分析的催化剂。镁试剂等。', '2018-04-13');
INSERT INTO `chemical` VALUES ('6', '原乙酸三甲酯', '', '2', '', '1003001001B2040131201', '1-3-1-2-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '120.00', 'C5H12O3', '120.15', '1445-45-0', '', '液体。对湿敏感。在水中分解。 相对密度 0.944。沸点 107～109℃。折光率 (n20D)1.3880。闪点 16℃。易燃。有刺激性。', '密封阴凉干燥保存。', '主要作为生产医药以及农药的化学中间体。用于合成维生素B1，维生素A1，磺胺啶等药物中间体，还可用于染料和香料工业。医药，家业化工，还可用于染料和香料工业。医药、农业化工、油漆添加剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('7', '硫酸汞', '', '1', '', '1003001001B2040131203', '1-3-1-2-3', '莫成昊', '2018-04-13', '是', '试剂', '否', '389.00', 'HgSO4', '296.65', '80-79260', '', '白色结晶或结晶性粉末。无气味。受热变为黄色至黄褐色但不分解,冷却后恢复为白色。溶于盐酸、热稀硫酸和浓氯化钠溶液,不溶于乙醇。能被水分解为黄色不溶性的碱式硫酸盐与游离硫酸。熔点分解。相对密度6.47。中等毒,半数致死量(大鼠,经口)57mG/kG。有腐蚀性。', '密封干燥避光保存。SCRC800792', '测定巴比妥和胱氨酸。与氯化钠同用,从焙烧黄铁矿中提炼金和银。催化剂。', '2018-06-09');
INSERT INTO `chemical` VALUES ('8', '还原铁粉', '', '1', '', '1003001001B2040131204', '1-3-1-2-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '68.00', 'Fe', '55.85', '7439-89-6', '', '灰色至灰黑色无定形细粒或粉末。有极微光泽。系氧化铁在700～900℃时用氢还原所得,内含90%～96%金属铁,其余主要为氧化亚铁。暴露于空气和湿气中易氧化。溶于稀酸,不溶于水。', '密封干燥保存。SCRC100118', '还原剂。电子工业。', '2018-04-13');
INSERT INTO `chemical` VALUES ('9', '二甲胺盐酸盐', '', '1', '', '1003001001B2040131205', '1-3-1-2-5', '莫成昊', '2018-04-13', '否', '试剂', '否', '32.00', 'C2H7NHCl', '81.54', '506-59-2', '', '白色叶状晶体。有挥发性。易潮解。易溶于水，溶于乙醇和氯仿，几乎不溶于乙醚。熔点171℃。有刺激性。', '密封干燥保存。SCRC300670', '有机合成。乙酰化分析的催化剂。镁试剂等。', '2018-04-13');
INSERT INTO `chemical` VALUES ('10', '丁二酮肟', '', '1', '', '1003001001B2040131206', '1-3-1-2-6', '莫成昊', '2018-04-13', '否', '试剂', '否', '32.00', 'C4H8N2O2', '116.12', '95-45-4', '', '白色三斜结晶或结晶性粉末。溶于乙醇、乙醚、丙酮和吡啶，几乎不溶于水。熔点238～240℃(也有报道为242℃和246℃)。最小致死量(大鼠，经口)250mg/kg。', '密封保存。SCRC100088', '镍的检定和测定。从钴及其他金属中分离镍，从锡、金、铼、铱中分离钯等。氰化物、镍、钯的光度测定。', '2018-04-13');
INSERT INTO `chemical` VALUES ('11', '邻氨基三氟甲苯', '', '1', '', '1003001001B2040131207', '1-3-1-2-7', '莫成昊', '2018-04-13', '否', '试剂', '否', null, 'C7H6F3N', '161.13', '88-17-5', '', '外观性状 无色或淡黄色液体，有刺激性熔点 34℃沸点 174℃闪点 131 °F密度 1.282 g/mL at 25 °C(lit.)', '请远离热源，火花和火焰。远离来源点火。储存在密闭的容器中。储存于阴凉，干燥，通风良好的地方远离不相容物质。从光存储受保护的', '【用途一】用作医药、农药及染料的中间体【用途二】用作塑料增塑剂产品的生产的中间体【用途三】该品是合成含氟除草剂和医药、染料的重要中间体', '2018-04-13');
INSERT INTO `chemical` VALUES ('12', '邻苯二甲酸氢钾', '', '2', '', '1003001001B2040131301', '1-3-1-3-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '51.00', 'C8H5KO4', '204.22', '877-24-7', '', '无色单斜结晶或白色结晶性粉末。在空气中稳定。溶于约12份冷水、3份沸水，微溶于乙醇。溶液呈酸性。25℃ 0.05mol/L水溶液的pH为4.005。相对密度(d254)1.636。熔点295～300℃(分解)。', '密封保存。SCRC 100159 301550', '分析试剂。标定碱的基准。配制缓冲溶液。', '2018-04-13');
INSERT INTO `chemical` VALUES ('13', '氯化钠', '', '2', '', '1003001001B2040131303', '1-3-1-3-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '20.00', 'NaCl', '58.44', '7647-14-5', '', '白色立方结晶或结晶性粉末。25℃时,1g溶于2.8ml水、2.6ml沸水、10ml甘油,极微溶于乙醇。其水中溶解度因盐酸存在而减少,几乎不溶于浓盐酸。水溶液呈中性,pH为6.7～7.3。相对密度 2.17。熔点 804℃。沸点 1413℃。半数致死量(大鼠,经口)3.75±0.43g/kg。', '密封保存。SCRC100193', '测定硝酸银的基准。微量测定氟和硅酸盐。用于高纯分 析。血液常规检验。肝功能试验。制备细菌血清学培养基。致冷混合剂。食物保存。照相。', '2018-04-13');
INSERT INTO `chemical` VALUES ('14', '无水碳酸钠', '', '1', '', '1003001001B2040131305', '1-3-1-3-5', '莫成昊', '2018-04-13', '否', '试剂', '否', '7.00', 'Na2CO3', '105.99', '497-19-8', '', '白色粉末或颗粒。无气味。有碱味。有吸湿性。露置空气中逐渐吸收 1mol/L水分(约15%)。400℃时开始失去二氧化碳。遇酸分解并泡腾。溶于水(室温时3.5份,35℃时2.2份)和甘油,不溶于乙醇。水溶液呈强碱性,pH 11.6。相对密度2.53。熔点851℃。半数致死量(30日)(小鼠,腹腔)116.6mg/kg。有刺激性。', '密封干燥保存。', '定量分析中标定酸液的 基准。测定铝、硫、铜、铅和锌。检验尿液和全血葡萄糖。分析水泥中二氧化硅的助熔剂。金属金相分析等。', '2018-04-13');
INSERT INTO `chemical` VALUES ('15', '甲醇钠', '', '1', '', '1003001001B2040131306', '1-3-1-3-6', '莫成昊', '2018-04-13', '是', '试剂', '否', '17.00', 'CH3ONa', '54.02', '124-41-4', '', '白色粉末。对空气与湿气敏感。遇水分解。溶于乙醇和甲醇。也有溶剂化合物的形态，如CH3ONa2CH3OH，为白色粉末。易燃。有腐蚀性。', '密封阴凉干燥保存。SCRC801175', '有机合成。', '2018-04-13');
INSERT INTO `chemical` VALUES ('16', '甲烷磺酸', '', '1', '', '1003001001B2040131307', '1-3-1-3-7', '莫成昊', '2018-04-13', '是', '试剂', '否', '109.00', 'CH4O3S', '96.11', '75-75-2', '', '白色到微黄色液体或固体。溶于水、乙醇和乙醚,26～28℃时微溶于苯、甲苯和邻氯甲苯,不溶于己烷和甲基环戊烷。在沸水、热碱液中不水解。能腐蚀铁、钢、铜、黄铜和铅。相对密度(d184)1.4812。熔点20℃。沸点167℃(1.33kPA)、122℃(0.13kPA)。折光率(n16D)1.4317。最小致死量(大', '密封阴凉保存。SCRC800800', '烷化、酯化和聚合反应催化剂。溶剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('17', '三氯蔗糖', '', '1', '', '1003001001B2040131308', '1-3-1-3-8', '莫成昊', '2018-04-13', '否', '试剂', '否', '280.00', 'C12H19Cl3O8', '', '56038-13-2', '', '通常为白色粉末状产品。物化性质比较接近蔗糖。耐高温、耐酸碱，温度和pH值对它几乎无影响，适于食品加工中的高温灭菌、喷雾干燥、焙烤、挤压等工艺。无热量、不致龋。pH适应性广，适用于酸性至中性食品，对涩、苦等不愉快味道有掩盖效果。易溶于水，溶解时不容易产生起泡现象，适用于碳酸饮料的高速灌装生产线[1]  。', '', '', '2018-04-13');
INSERT INTO `chemical` VALUES ('18', '亚甲基蓝', '', '1', '', '1003001001B2040131309', '1-3-1-3-9', '莫成昊', '2018-04-13', '否', '试剂', '否', '180.00', 'C16H18ClN3S·3H2O', '373.9', '61-73-4', '', '发亮深绿色结晶或细小深褐色粉末,带青铜光泽。无气味。在空气中稳定。1G溶于约25ml水、约65ml乙醇,溶液为天蓝色,溶于氯仿,不溶于乙醚和苯。水溶液遇锌粉和稀硫酸能褪色,但暴露空气中能恢复,若加氨水则恢复更快。能与多数无机盐生成复盐。最大吸收波长668、609nm。低毒,半数致死量(大鼠,经口)1180mG/kG。', '密封干燥保存。SCRC301801', '测定乙酰基和伯胺类。有机合成。催化剂。染料工业。', '2018-04-13');
INSERT INTO `chemical` VALUES ('19', '甲基橙(对二甲基氨基偶氮苯磺酸钠)', '', '1', '', '1003001001B2040131402', '1-3-1-4-2', '莫成昊', '2018-04-13', '否', '试剂', '否', '17.00', 'C14H14N3NaO3S', '327.33', '547-58-0', '', '橙黄色粉末或鳞片状结晶，1份溶于500份水中，易溶于热水，溶液呈金黄色，几乎不溶于乙醇；最大吸收波长505nm', '密封阴凉干燥保存。SCRC710251', '酸碱指示剂,pH值变色范围3.1(红)～4.4(黄),测定多数矿酸、强碱和水的碱度。容量测定锡(热时Sn2+使甲基橙褪色)。强还原剂(Ti3+、Cr2+)和强氧化剂(氯、溴)的消色指示剂。分光光度测定氯、溴和溴离子。可与靛蓝二磺酸钠或', '2018-04-13');
INSERT INTO `chemical` VALUES ('20', '甲苯-4-磺酸，一水', '', '1', '', '1003001001B2040131401', '1-3-1-4-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '18.00', 'C7H8O3SH2O', '190.22', '6192-52-5', '', '白色棱柱体叶状或针状结晶或粉末。有吸湿性。易溶于水，溶于乙醇和乙醚，微溶于苯和甲苯。熔点106～107℃(无水物)。沸点140℃(2.66kPa)。闪点150℃。低毒，半数致死量(大鼠，经口)2480mg/kg。有刺激性。商品也有含4分子结晶水的。', '密封干燥保存。SCRC301801', '测定乙酰基和伯胺类。有机合成。催化剂。染料工业。', '2018-04-13');
INSERT INTO `chemical` VALUES ('21', '溴百里酚蓝', '', '1', '', '1003001001B2040131403', '1-3-1-4-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '117.00', 'C27H28Br2O5S', '624.38', '76-59-5', '', '近白色或乳色结晶。溶于乙醇、乙醚、甲醇和稀氢氧化碱溶液,略溶于苯、甲苯和二甲苯,微溶于水,几乎不溶于石油醚。熔点200～202℃。最大吸收波长420nm。', '密封阴凉干燥保存。SCRC710084', '酸碱指示剂,pH变色范围6.0(黄色)～7.6(蓝色)。吸附指示剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('22', '硫酸银', '', '2', '', '1003001001B2040131404', '1-3-1-4-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '238.00', 'Ag2SO4', '311.8', '10294-26-5', '', '无色结晶或白色结晶性粉末。遇光逐渐变黑色。在1085℃分解。溶于硝酸、氨水和浓硫酸,慢慢地溶于125份水和71份沸水,不溶于乙醇。相对密度(d29)5.45。熔点657℃。有刺激性。', '密封干燥避光保存。SCRC200369', '比色测定亚硝酸盐、钒酸盐、磷酸盐和氟。测定乙烯。水质分析中铬和钴的测定。', '2018-04-13');
INSERT INTO `chemical` VALUES ('23', '1,10-菲啰啉，一水', '', '1', '', '1003001001B2040131406', '1-3-1-4-6', '莫成昊', '2018-04-13', '否', '试剂', '否', '17.00', 'C12H8N2H2O', '198.22', '5144-89-8', '', '白色结晶性粉末。溶于约300份水、70份苯，溶于乙醇和丙酮，不溶于乙醚。熔点93～94℃。沸点300℃。贮存后能变成奶色。', '密封保存。SCRC301415', '氧化还原指示剂。亚铁试剂。生化研究。铂的萃取。重量法测定钯。掩蔽剂。比色测定二价铁、铂、钯等。', '2018-04-13');
INSERT INTO `chemical` VALUES ('24', '磷酸二氢钾', '', '1', '', '1003001001B2040132101', '1-3-2-1-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '22.00', 'KH2PO4', '136.09', '7778-77-0', '', '无色结晶或白色颗粒状粉末。在空气中稳定，在400℃时失去水，变成偏磷酸盐。溶于约4.5份水，不溶于乙醇。pH4.4～4.7。相对密度2.34。熔点252.6℃。', '密封保存。SCRC100176510205', '配制缓冲液。测定砷、锑、磷、铝和铁。配制磷标准液。配制培养基。测定血清中无机磷、碱性磷酸酶活力。', '2018-04-13');
INSERT INTO `chemical` VALUES ('25', '硝酸钾', '', '1', '', '1003001001B2040132102', '1-3-2-1-2', '莫成昊', '2018-04-13', '否', '试剂', '否', '25.00', 'KNO3', '101.1', '7757-79-1', '', '无色透明棱柱状或白色颗粒或结晶性粉末。味辛辣而咸有凉感。微吸湿，吸湿性比硝酸钠为小。在400℃时分解而逸出氧。1g溶于2.8ml水、0.5ml沸水、620ml乙醇。溶于甘油，不溶于无水乙醇。溶于水时能吸收热量降低温度。水溶液pH约为7。相对密度(d164)2.109。熔点334℃。有强氧化性', '密封阴凉干燥保存。SCRC100172801065', '强氧化剂。助熔剂。用于发射光谱分析。点滴分析锰和钠。钾盐合成。照相用。', '2018-04-13');
INSERT INTO `chemical` VALUES ('26', '氯化铵', '', '1', '', '1003001001B2040132103', '1-3-2-1-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '13.00', 'NH4Cl', '53.49', '12125-02-9', '', '无色结晶或白色颗粒性粉末。无气味。有阴凉咸味。略吸湿。能升华而无熔点。盐酸和氯化钠能降低其水中溶解度,其水中溶解度在-13℃时为20%(w/w),10℃时为25%,33℃时为30%,80℃时为40%。有强吸热性(即溶解时,降低溶液温度)。水溶液的pH：25℃1%为5.5,3%为5.1,10%为5.0。溶于甲醇', '密封保存。SCRC100015510011', '分离及沉淀离子。配制缓冲液。测定尿酸。冷冻剂。制药。', '2018-04-13');
INSERT INTO `chemical` VALUES ('27', '四水合酒石酸钾钠', '', '1', '', '1003001001B2040132104', '1-3-2-1-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '40.00', 'C4H4KNaO64H2O', '282.22', '6381-59-5', '', '无色半透明结晶或白色结晶性粉末。味咸而凉。在暖空气中稍有风化，100℃时失去3分子水，130～140℃失去全部水分，220℃开始分解。溶于0.9份水，几乎不溶于乙醇。水溶液对石蕊呈微碱性，pH7～8。相对密度1.79。熔点70～80℃。', '密封保存。SCRC100178', '配制斐林氏溶液。掩蔽剂。络合剂。尿酸和糖的测定。血清蛋白的生化检验。', '2018-04-13');
INSERT INTO `chemical` VALUES ('28', '聚乙二醇', '', '1', '', '1003001001B2040132201', '1-3-2-2-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '38.00', 'H(OCH2CH2)nOH', 'NA', '25322-68-3', '', '白色或浅黄色蜡状固体，或为片状、颗粒状。有轻微的特殊气味。溶于水和乙醇。相对密度(d2525)1.21。熔点56～63℃。粘度(99℃)470～900μm2/s。闪点(开杯)>119℃。半数致死量(大鼠，经口)>50g/kg。', '密封保存。SCRC301517400443', '气相色谱固定液(最高使用温度130℃，溶剂为丙酮、氯仿、二氯甲烷)，分离分析含氧化合物(如醇、醛)、维生素、脂肪酸酯、水和农药。水溶性润滑剂。药用辅料。', '2018-04-13');
INSERT INTO `chemical` VALUES ('29', '酒石酸锑钾，半水', '', '1', '', '1003001001B2040132202', '1-3-2-2-2', '莫成昊', '2018-04-13', '是', '试剂', '否', '137.00', 'C4H4KO7Sb0.5H2O', '333.93', '16039-64-8', '', '无色透明结晶或白色粉末。无臭。微甜且有金属味。在空气中风化', '密封保存。SCRC100035', '测定铅。分析钠、锗。媒染剂。杀虫剂。', '2018-06-09');
INSERT INTO `chemical` VALUES ('30', '铬酸钾', '', '1', '', '1003001001B2040132203', '1-3-2-2-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '51.00', 'K2CrO4', '194.19', '7789-00-6', '', '柠檬黄色斜方结晶。溶于1.6份冷水、1.2份沸水，不溶于乙醇。水溶液对石蕊和酚酞呈碱性。相对密度2.73。熔点975℃。有氧化性。半数致死量(兔，肌肉)11mg/kg。经流行病学调查表明，对人有潜在致癌危险性。有刺激性。', '密封保存。SCRC100164200307', '点滴分析测定钡、银。测定农药DDT和五氯酚钠的含量。发射光谱分析。氧化还原基准试剂。氧化剂。媒染剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('31', '六水合硫酸铁（2）铵', '', '1', '', '1003001001B2040132204', '1-3-2-2-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '20.00', '(NH4)2Fe(SO4)26H2O', '392.14', '7783-85-9', '', '浅蓝绿色结晶或粉末。对光敏感。在空气中逐渐风化及氧化。溶于水,几乎不溶于乙醇。相对密度(d204)1.86。低毒,半数致死量(大鼠,经口)3250mG/kG。有刺激性。', '密封干燥避光保存。SCRC100019', '用以标定高锰酸钾溶液。配制亚铁离子标准溶液。用作聚合催化剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('32', '无水葡萄糖', '', '1', '', '1003001001B2040132301', '1-3-2-3-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '22.00', 'C6H12O6', '180.16', '50-99-7', '', '白色结晶性粉末。味甜。有吸湿性。25℃时1G溶于1.1ml水、30℃时溶于0.8ml水、50℃时溶于0.41ml水、70℃时溶于0.28ml水、90℃时溶于0.18ml水,溶于热冰乙酸、吡啶和苯胺,20℃时溶于120ml甲醇,极微溶于无水乙醇、乙醚和丙酮。0.5mol/L水溶液pH5.9。熔点146℃。折光率(n20D)1.13', '密封阴凉干燥保存。SCRC630055', '生物培养基。微量分析。还原剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('33', '氢氧化钠', '', '1', '', '1003001001B2040132302', '1-3-2-3-2', '莫成昊', '2018-04-13', '是', '试剂', '否', '15.00', 'NaOH', '40', '1310-73-2', '', '熔融白色颗粒或条状，现常制成小片状。易吸收空气中的水分和二氧化碳。1g溶于0.9ml冷水、0.3ml沸水、7.2ml无水乙醇、4.2ml甲醇，溶于甘油。溶于水、乙醇时或溶液与酸混和时产生剧热。溶液呈强碱性。相对密度(d25)2.13。熔点318℃。沸点1390℃。半数致死量(小鼠，腹腔', '密封干燥保存。SCRC100197', '【用途】广泛应用的基本分析试剂。配制分析用标准碱液。少量二氧化碳和水分的吸收剂。酸的中和。钠盐制造。', '2018-04-13');
INSERT INTO `chemical` VALUES ('34', '重铬酸钾', '', '1', '', '1003001001B2040132303', '1-3-2-3-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '53.00', 'K2Cr2O7', '294.18', '7778-50-9', '', '橙红色有光泽结晶颗粒或粉末。味苦。不吸湿或潮解(不同于重铬酸钠)。强热约500℃时分解为氧化铬和铬酸钾。在盐酸中冷时不起作用，热时则发生氯气。溶于10份冷水或1.25份热水，不溶于乙醇，水溶液呈酸性反应。相对密度(d254)2.676。熔点398℃(成褐色液体)。有强氧化性，与有机', '密封阴凉保存。SCRC100166801057', '标定硫代硫酸钠标准溶液的基准。点滴分析定氯。氧化剂。色谱分析用试剂。有机合成。测定肝功能黄胆指数。检验粪便等。', '2018-04-13');
INSERT INTO `chemical` VALUES ('35', '十水合硫酸亚铁', '', '1', '', '1003001001B2040132304', '1-3-2-3-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '12.00', 'FeSO47H2O', '278.01', '7782-63-0', '', '蓝绿色单斜结晶或颗粒。无气味。在干燥空气中风化。在潮湿空气中表面氧化成棕色的碱式硫酸高铁。在56.6℃成为四水合物,在65℃时成为一水合物。溶于水,几乎不溶于乙醇。其水溶液冷时在空气中缓慢氧化,在热时较快氧化。加入碱或露光能加速其氧化。相对密度(d15)1.897。半数致死', '密封阴凉干燥避光保存。SCRC100121510130', '色谱分析试剂。点滴分析测定铂、硒、亚硝酸盐和硝酸盐。还原剂。制造铁氧体。净水。聚合催化剂。照相制版。', '2018-04-13');
INSERT INTO `chemical` VALUES ('36', '四水合钼酸铵', '', '1', '', '1003001001B2040132401', '1-3-2-4-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '237.00', '(NH4)6Mo7O244H2O', '1235.86', '12054-85-2', '', '无色至微绿或微黄结晶。露置于空气中易风化,并失去一部分氨。加热至90℃时,失去一分子结晶水,190℃时分解为氨、水和三氧化钼。溶于2.3份水,几乎不溶于乙醇。5%水溶液的pH为5～5.5。相对密度2.498。有毒,半数致死量(大鼠,经口)333mG/kG。有刺激性。', '密封阴凉干燥保存。SCRC100023', '测定磷酸盐、砷酸盐、铅、生物碱。钼蓝法测定土壤及植株的含磷量。测定血清中无机磷、碱性磷酸酶活力。测定钢铁中硅、磷成分。用于陶瓷釉彩。', '2018-04-13');
INSERT INTO `chemical` VALUES ('37', '过二硫酸钾', '', '1', '', '1003001001B2040132402', '1-3-2-4-2', '莫成昊', '2018-04-13', '是', '试剂', '否', '31.00', 'K2S2O8', '270.32', '7727-21-1', '', '无色或白色结晶。无气味。能逐渐分解失去有效氧，湿气中能促使其分解，高温时分解较快，在约100℃时全部分解。溶于约50份水(40℃时溶于25份水)，不溶于乙醇，水溶液呈酸性。相对密度2.477。有强氧化性。与有机物摩擦或撞击能引起燃烧。有强刺激性。', '密封阴凉干燥保存。SCRC100174', '分析试剂。氧化剂。聚合反应引发剂。照相冲洗(除去硫代硫酸盐)。', '2018-04-13');
INSERT INTO `chemical` VALUES ('38', '二水合氯化亚锡', '', '1', '', '1003001001B2040132403', '1-3-2-4-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '141.00', 'SnCl22H2O', '225.65', '10025-69-1', '', '无色单斜棱柱体结晶。结晶或溶液都能从空气中吸收氧成为不溶性氧氯化物。强热时分解。极易溶于稀的或浓的盐酸，溶于小于本身重量的水，当溶于大量水时，形成不溶性的碱式盐，溶于乙醇、乙酸乙酯、冰乙酸和氢氧化钠溶液。加微量的金属锡，可预防溶液的氧化作用。相对密度(d', '密封阴凉干燥保存。SCRC100225', '强还原剂。比色测定银、铅、砷和钼。测定血清中无机磷及碱性磷酸酯酶活力。钼蓝法测定土壤及植株的含磷量。有机反应催化剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('39', '过氧化氢（30%）', '', '1', '', '1003001001B2040132404', '1-3-2-4-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '12.80', 'H2O2', '34.01', '7722-84-1', '', '无色透明液体，味苦，能缓慢分解成氧和水，少量不纯物质存在时剧烈分解，碱性及金属细粉能加速其分解，亦能被多数有机溶剂分解，可加入少量N-乙酰苯胺和N-乙酰乙氧基苯胺作稳定剂。能与水混溶，溶于乙醚，不溶于石油醚。水溶液呈弱酸性。有氧化性和腐蚀性，对皮肤有化学浸蚀性，能与水混合。密度：1.11熔点：-0.43℃沸点：152℃折光率：1.3350', '用瓶口有微孔的塑料瓶装阴凉保存。SCRC100112800709', '氧化剂。漂白剂。点滴分析测定钙、钴、铜、锰、钛、钒、铵及铬酸。有机合成。', '2018-04-13');
INSERT INTO `chemical` VALUES ('40', '氯化铵', '', '1', '', '1003001001B2040132405', '1-3-2-4-5', '莫成昊', '2018-04-13', '否', '试剂', '否', '11.00', 'NH4Cl', '53.49', '12125-02-9', '', '无色结晶或白色颗粒性粉末。无气味。有阴凉咸味。略吸湿。能升华而无熔点。盐酸和氯化钠能降低其水中溶解度,其水中溶解度在-13℃时为20%(w/w),10℃时为25%,33℃时为30%,80℃时为40%。有强吸热性(即溶解时,降低溶液温度)。水溶液的pH：25℃1%为5.5,3%为5.1,10%为5.0。溶于甲醇和乙醇,几乎不溶于丙酮、乙醚和乙酸乙酯。相对密度(d25)1.5274。折光率1.642。低毒,半数致死量(大鼠,经口)1650mG/kG。有刺激性。', '密封保存。SCRC100015510011', '分离及沉淀离子。配制缓冲液。测定尿酸。冷冻剂。制药。', '2018-04-13');
INSERT INTO `chemical` VALUES ('41', '对苯二甲酸', '', '1', '', '1003001001B2040132406', '1-3-2-4-6', '莫成昊', '2018-04-13', '否', '试剂', '否', '103.00', 'C8H6O4', '166.13', '100-21-0', '', '白色针状结晶或粉末。约300℃升华不熔融。易溶于热乙醇，溶于碱，微溶于冷乙醇，几乎不溶于水、氯仿、乙醚和乙酸。相对密度 1.510。熔点425℃(封管中)。', '密封保存。SCRC 301480 400412', '有机合成。测定羊毛的残留碱度。气相色谱扫尾剂。加入聚乙二醇中，可分离分析脂肪酸。', '2018-04-13');
INSERT INTO `chemical` VALUES ('42', '高氯酸钠', '', '2', '', '1003001001B2040132407', '1-3-2-4-7', '莫成昊', '2018-04-13', '否', '试剂', '否', '145.00', 'NaClO4H2O', '140.46', '7791-7-3', '', '白色结晶。有吸湿性。约在130℃分解。易溶于水，溶于乙醇和丙酮，不溶于乙醚。相对密度2.02。有氧化性。与有机物摩擦或撞击能引起燃烧或爆炸，接触浓硫酸也能引起爆炸。低毒，半数致死量(大鼠，经口)2100mg/kg。有刺激性。', '密封干燥保存。SCRC801182', '分析试剂。爆炸品工业。氧化剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('43', '氧化锌；一氧化锌', '', '2', '', '1003001001B2040242102', '2-4-2-1-2', '莫成昊', '2018-04-13', '否', '试剂', '否', '38.00', 'ZnO', '81.39', '1314-13-2', '', '白色、浅黄色粉末或六方结晶。无气味。味苦。在正常压力下能升华。能吸收空气中的二氧化碳。加热至300℃色变黄，但冷却后又成白色。溶于稀乙酸、矿酸、氨水、碳酸铵和氢氧化碱溶液，几乎不溶于水。相对密度5.67(六方结晶)，(d204)5.607。熔点1800℃以上。折光率(nD)2.00', '密封阴凉保存。SCRC100239200456', '标定乙二胺四乙酸二钠的基准物质。在锰的氧化还原容量法测定中用以沉淀盐类易水解的元素，如铁、铬、钒、钛和锆。用作硫化氢吸收剂。颜料。半导体。', '2018-04-13');
INSERT INTO `chemical` VALUES ('44', '氧化硼(Ⅲ)；三氧化二硼；硼酐', '', '1', '', '1003001001B2040242103', '2-4-2-1-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '57.00', 'B2O3', '69.62', '1303-86-2', '', '白色结晶性粉末或无色半透明玻璃状固体。性硬而脆。微有苦味。有吸湿性。溶于乙醇和甘油,缓慢溶于30份冷水、5份沸水。相对密度1.8(无定形)、2.46(结晶)。熔点450℃(结晶)。有刺激性。', '密封阴凉干燥保存。SCRC200099', '硅酸盐分析中测定二氧化硅和碱。吹管分析。分解硅酸盐的助熔剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('45', '氧化铬(Ⅲ)；三氧化二铬', '', '1', '', '1003001001B2040242104', '2-4-2-1-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '60.00', 'Cr2O3', '151.99', '1308-38-9', '', '浅绿至深绿色细小六方结晶。灼热时变棕色，冷后仍变为绿色。结晶体极硬。性极稳定，即使在红热下通入氢气亦无变化。溶于加热的溴酸钾溶液，微溶于酸类和碱类，几乎不溶于水、乙醇和丙酮。相对密度(d25)5.22。熔点约2435℃。沸点约3000℃。有刺激性。', '密封保存。SCRC100069', '有机和无机反应催化剂。耐火材料。磨料。铬合金、铬盐和着色玻璃制造。', '2018-04-13');
INSERT INTO `chemical` VALUES ('46', '乙二酸，二水；草酸', '', '1', '', '1003001001B2040242201', '2-4-2-2-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '17.00', 'C2H2O42H2O', '126.07', '6153-56-6', '', '无色单斜片状或棱柱体结晶或白色粉末。无气味。150～160℃升华。在高热干燥空气中能风化。1g溶于7ml水、2ml沸水、2.5ml乙醇、1.8ml沸乙醇、100ml乙醚、5.5ml甘油，不溶于苯、氯仿和石油醚。0.1mol/L溶液的pH值为1.3。相对密度(d18.54)1.653。熔点101～102℃(187℃，无水)。低', '密封保存。SCRC100148301388', '络合剂。掩蔽剂。沉淀剂。还原剂。分析中用以检定和测定铍、钙、铬、金、锰、锶、钍等金属离子。显微微晶分析检验钠和其他元素。沉淀钙、镁、钍和稀土元素。校准高锰酸钾和硫酸铈溶液的标准溶液。漂白剂。助染剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('47', '氧化铜(Ⅱ)；一氧化铜', '', '2', '', '1003001001B2040242203', '2-4-2-2-3', '莫成昊', '2018-04-13', '否', '试剂', '否', '94.00', 'CuO', '79.55', '1317-38-0', '', '黑色至棕黑色无定形或结晶性粉末。商品尚有粒状、线状等。稍有吸湿性。溶于稀酸、氰化碱或碳酸铵溶液，缓慢溶于氨水，几乎不溶于水和乙醇。相对密度(d14４)6.315。熔点1026℃。有刺激性。', '密封干燥保存。SCRC100080', '气体分析中测定碳。有机反应催化剂。制造人造丝和其他铜化合物。光学玻璃发光剂。作为色料用于人造宝石、有色玻璃、陶瓷釉彩。', '2018-04-13');
INSERT INTO `chemical` VALUES ('48', '氟化铵', '', '1', '', '1003001001B2040242204', '2-4-2-2-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '36.00', 'NH4F', '37.04', '12125-01-8', '', '白色针状结晶。易吸湿。溶于冷水,微溶于乙醇。加热分解为氨和氟化氢。在热水中分解为氨及氟化氢铵。其水溶液呈酸性。能腐蚀玻璃。相对密度1.015。有毒,半数致死量(大鼠,腹腔)32mG/kG。有腐蚀性。', '用塑料容器盛装。密封阴凉干燥保存。SCRC100020', '络合滴定铝的沉淀剂。测定铜合金中铅、铜、锌。测定铁矿石,煤焦含铝量。萃取稀有元素。玻璃镂刻。木材防腐。', '2018-04-13');
INSERT INTO `chemical` VALUES ('49', '磷酸；正磷酸', '', '2', '', '1003001001B2040242301', '2-4-2-3-1', '莫成昊', '2018-04-13', '否', '试剂', '否', '17.00', 'H3PO4', '98', '7664-38-2', '', '无水时为不稳定的斜方结晶(熔点42.35℃)或无色透明粘稠液体。容易过冷成玻璃状。无气味。味酸。适当的稀释后有舒适的酸味。88％的磷酸长时间放在冷处，常生成结晶(熔点29.32℃，半水磷酸)。在150℃时变为无水物，约在200℃时逐渐变为焦磷酸。高于300℃时变为偏磷酸。溶于水', '密封阴凉干燥保存。SCRC100154801017', '测定钢铁中铬、镍、钒。金属防锈。制造乙醇用催化剂。测定血清中非蛋白氮、尿素氮、总胆固醇和全血葡萄糖。', '2018-04-13');
INSERT INTO `chemical` VALUES ('50', '铝酸钠；偏铝酸钠', '', '1', '', '1003001001B2040242304', '2-4-2-3-4', '莫成昊', '2018-04-13', '否', '试剂', '否', '48.00', 'NaAlO2', '81.97', '1302-42-7', '', '白色颗粒。易吸湿。极易溶于水,不溶于乙醇,水溶液呈强碱性。熔点1650℃。', '密封干燥保存。SCRC200373', '媒染剂。软水剂。制造沸石、乳白玻璃和肥皂。', '2018-04-13');
