INSERT INTO `chemical` VALUES ('53', '二甲基硅油', '', '1', '', '1003001001B001021101', '2-1-1-1', '莫成昊', '2018-04-13', '否', '试剂', '是', '110.00', '[OSi(CH3)2]n', 'NA', '9016-00-6', '', '无色透明油状液体。为苯基含量较高的硅油。溶于乙醚,不溶于水。相对密度(d204)1.066。黏度38mPas。', '密封保存。SCRC400555', '气相色谱固定液(最高使用温度280℃,溶剂为乙醚)。', '2018-04-13');
INSERT INTO `chemical` VALUES ('54', '十二烷基苯磺酸钠', '', '1', '', '1003001001B001021102', '2-1-1-2', '莫成昊', '2018-04-13', '否', '试剂', '是', '35.00', 'C18H29NaO3S', '348.48', '25155-30-0', '', '白色或浅黄色粉末或颗粒。溶于水、热乙醇。其水溶液极易起泡，但粘度较低，且易消失，有较好的渗透力和去污力。有毒，半数致死量(小鼠，经口)2g/kg。有刺激性。', '密封阴凉干燥保存。SCRC301663', '阴离子型表面活性剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('55', '氧化锌', '', '1', '', '1003001001B001021103', '2-1-1-3', '莫成昊', '2018-04-13', '否', '试剂', '是', '38.00', 'ZnO', '81.39', '1314-13-2', '', '性状：白色、浅黄色粉末或六方结晶。无气味。味苦。在正常压力下能升华。能吸收空气中的二氧化碳。加热至300℃色变黄，但冷却后又成白色。溶于稀乙酸、矿酸、氨水、碳酸铵和氢氧化碱溶液，几乎不溶于水。相对密度5.67(六方结晶)，(d204)5.607。熔点1800℃以上。折光率(nD)2.00', '储存：密封阴凉保存。SCRC100239200456', '用途：标定乙二胺四乙酸二钠的基准物质。在锰的氧化还原容量法测定中用以沉淀盐类易水解的元素，如铁、铬、钒、钛和锆。用作硫化氢吸收剂。颜料。半导体。', '2018-04-13');
INSERT INTO `chemical` VALUES ('56', '氧化钙', '', '1', '', '1003001001B001021104', '2-1-1-4', '莫成昊', '2018-04-13', '否', '试剂', '是', '18.00', 'CaO', '56.08', '1305-78-8', '', '白色或带灰色块状或颗粒。对湿敏感。易从空气中吸收二氧化碳及水分。溶于水成氢氧化钙并产生大量热，溶于酸类、甘油和蔗糖溶液，几乎不溶于乙醇。相对密度3.32～3.35。熔点2572℃。沸点2850℃。折光率1.838。有腐蚀性。', '密封干燥保存。SCRC100059800291', '分析试剂，实验中用作二氧化碳吸收剂。助熔剂。植物油脱色剂。', '2018-04-13');
