INSERT INTO `chemical` VALUES ('78', '三氧化铬', '', '1', '', '1003001001B001021308', '2-1-3-8', '莫成昊', '2018-04-13', '是', '试剂', '是', '51.00', 'CrO3', '99.99', '1333-82-0', '', '深红色双锥体棱晶、片状结晶或颗粒。易吸湿。在250℃分解成三氧化二铬和氧。易溶于水，溶于硫酸。有氧化性，能氧化乙醇和其他有机物质而引起燃烧。相对密度2.70。熔点197℃。有毒，最小致死量(狗，皮下)330mg/kg。有致癌可能性。', '', '', '2018-04-13');
INSERT INTO `chemical` VALUES ('79', '无机粘结剂：A氯化铜', '', '1', '', '1003001001B001021309', '2-1-3-9', '莫成昊', '2018-04-13', '否', '试剂', '是', null, '', '', '', '', '', '', '', '2018-04-13');
INSERT INTO `chemical` VALUES ('80', 'MQ102', '', '1', '', '1003001001B001021401', '2-1-4-1', '莫成昊', '2018-04-13', '否', '试剂', '是', null, '', '', '', '', '', '', '', '2018-04-13');
INSERT INTO `chemical` VALUES ('81', 'N-N\'亚甲基双丙烯酰胺', '', '1', '', '1003001001B001021402', '2-1-4-2', '莫成昊', '2018-04-13', '否', '试剂', '是', '28.00', 'C7H10N2O2', '154.17', '110-26-9', '', '白色粉末状结晶。无气味。对光敏感。20℃时水中可溶0.31G/ml,水溶液可因水解而形成丙烯酸和氨。熔点>300℃。中等毒,半数致死量(大鼠,经口)390mG/kG。有刺激性。', '密封4℃避光保存。SCRC301178', '生物高分子化合物(蛋白质、多肽、核酸)的分离。制备聚丙烯酰胺凝胶的交联剂。聚丙烯酰胺凝胶技术和临床检验。', '2018-04-13');
INSERT INTO `chemical` VALUES ('82', '石油醚', '', '2', '', '1003001001B001022101', '2-2-1-1', '莫成昊', '2018-04-13', '是', '试剂', '是', '121.00', 'NA', 'NA', '8032-32-4', '', '无色透明液体。有特殊气味。无荧光。易挥发。低粘度。在冷时不凝固。能与无水乙醇、苯、氯仿、乙醚、二硫化碳、四氯化碳和油类(蓖麻油除外)混溶，不溶于水。相对密度0.625～0.660。沸点30～120℃。易燃。其蒸气与空气混合灼烧能引起爆炸。', '密封阴凉保存。对低沸程石油醚应远离火种。SCRC100152800986-8', '溶剂。去垢剂。萃取剂。色谱分析的展开剂等。', '2018-04-13');
INSERT INTO `chemical` VALUES ('83', '四氯化碳', '', '1', '', '1003001001B001022103', '2-2-1-3', '莫成昊', '2018-04-13', '是', '试剂', '是', '70.00', 'CCl4', '153.82', '56-23-5', '', '无色澄清重质不燃液体。有特殊气味。蒸气比空气重。有湿气存在时逐渐分解成有剧毒的光气和氯化氢。能与乙醇、乙醚、二硫化碳、石油醚、氯仿、苯和油等混溶，微溶于水(1∶2000)。相对密度(d2525)1.589。凝固点-23℃。沸点76.7℃。折光率(n２０Ｄ)1.4607。低毒，半数致死量(大鼠，经口)2920mg/kg。经试验表明，对实验动物有潜在致癌作用。', '密封阴凉干燥避光保存。SCRC100064800305', '分析中用作脂肪、树脂、树胶等不燃性溶剂。提取带色的各种金属和某些络合物的二苯硫代偶氮肼羰化合物。检定硼、溴、钙、铜、碘和镍。测定硼、溴、氯、钼、磷、银、钨和钒。香花和种子的油质浸出剂。有机微量分析测定氯的标准。电子工业清洗剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('84', '无水乙醚', '', '1', '', '1003001001B001022104', '2-2-1-4', '莫成昊', '2018-04-13', '是', '试剂', '是', '18.50', 'C4H10O', '74.12', '60-29-7', '', '同乙醚，但水分含量较少。剧烈振摇时可能产生静电而起火。', '密封阴凉保存。SCRC800596', '溶剂。分析油脂和氨基酸。有机合成。', '2018-04-13');
INSERT INTO `chemical` VALUES ('85', '甲醇', '', '3', '', '1003001001B001022105', '2-2-1-5', '莫成昊', '2018-04-13', '是', '试剂', '是', '11.00', 'CH4O', '32.04', '67-56-1', '', '无色澄清液体。微有乙醇样气味。易挥发。易流动。燃烧时无烟有蓝色火焰。能与多种化合物形成共沸混合物。能与水、乙醇、乙醚、苯、酮类和其他有机溶剂混溶。溶解性能优于乙醇,能溶解多种无机盐类,如碘化钠、氯化钙、硝酸铵、硫酸铜、硝酸银、氯化铵和氯化钠等。相对密度(d204', '密封阴凉保存。SCRC100141', '分离硫酸钙和硫酸镁。与异丁醇混合分离锶和钡的溴化物。检验和测定硼。溶剂。有机合成的甲基化剂。防冻剂。浸出剂。萃取剂。清洗剂。稀释剂。脱水剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('86', '甲醛', '', '3', '', '1003001001B001022108', '2-2-1-8', '莫成昊', '2018-04-13', '是', '试剂', '是', '108.00', 'CH2O', '30.03', '50-00-0', '', '无色液体。本品为约含37%甲醛(HCHO)气体的水溶液(重量计),一般含10%～15%的甲醇,以防止其聚合。有刺激性气味。在较冷时久贮易混浊,在低温时则形成三聚甲醛沉淀。蒸发时有一部分甲醛逸出,但多数变成三聚甲醛。本品为强还原剂,在微量碱性时还原性更强。在空气中能缓慢氧化成甲', '密封避光在16℃以上的温处保存。低温处不宜久贮。SCRC100100', '分析测定铵盐和氨基酸。显微分析中各种制剂的固定剂。消毒剂。杀菌剂。还原剂。', '2018-04-13');
INSERT INTO `chemical` VALUES ('87', '二氯甲烷', '', '1', '', '1003001001B001022111', '2-2-1-11', '莫成昊', '2018-04-13', '是', '试剂', '是', '20.00', 'CH2Cl2', '84.93', '27639', '', '无色液体。有醚样气味。易挥发。其气体不燃烧，与空气混合也不爆炸。能与乙醇、乙醚和N,N捕甲基甲酰胺混溶，溶于约50份水中。相对密度(d204)1.3255。凝固点-95℃。沸点39.75℃。折光率(n20D)1.4244。低毒，半数致死量(大鼠，经口)2524mg/kg。高浓度蒸气有麻醉性。有刺激性', '密封阴凉避光保存。SCRC400135800473', '乙酸纤维素溶剂。脂肪和油类萃取剂。乙醚和石油醚的代用品。', '2018-04-13');
INSERT INTO `chemical` VALUES ('88', '三氯甲烷', '', '1', '', '1003001001B001022201', '2-2-2-1', '莫成昊', '2018-04-13', '是', '试剂', '是', '35.00', 'CHCl3', '119.38', '67-66-3', '', '无色透明液体。有特殊气味。味甜。高折光，不燃，质重，易挥发。纯品对光敏感，遇光照或与空气中的氧作用，逐渐分解而生成剧毒的光气(碳酰氯)和氯化氢。可加入0.6%～1％的乙醇作稳定剂。能与乙醇、苯、乙醚、石油醚、四氯化碳、二硫化碳和油类等混溶、25℃时1ml溶于200ml水', '密封阴凉避光保存。SCRC100068', '脂肪、橡胶、树脂、油类、蜡、磷和碘的溶剂。青霉素、精油、生物碱等的萃取剂。校正温度计。测定血清中无机磷。清洗剂。肝功能试验的防腐剂。有机合成。', '2018-04-13');
INSERT INTO `chemical` VALUES ('89', '乙腈', '', '1', '', '1003001001B001022202', '2-2-2-2', '莫成昊', '2018-04-13', '否', '试剂', '是', '42.00', '', '', '', '', '', '', '', '2018-04-13');
INSERT INTO `chemical` VALUES ('90', '异丙醇', '', '1', '', '1003001001B001022203', '2-2-2-3', '莫成昊', '2018-04-13', '否', '试剂', '是', '21.00', 'C3H8O', '60.1', '67-63-0', '', '无色透明液体。有类似乙醇、丙酮混合的气味。味微苦。能与水、乙醇、乙醚和氯仿混溶,不溶于盐溶液。能与水形成共沸混合物(含水12.3%),共沸点80.37℃。易生成过氧化物。相对密度(d204)0.78505。熔点-88.5℃。凝固点89.5℃。沸点82.5℃。折光率(n20D)1.37723。闪点(闭杯)12℃。', '密封保存。SCRC400499801092', '杂酚油、虫胶、树脂、树胶、硝基纤维素的溶剂。酯化分析测定植物油的溶剂。测定钡、钙、铜、镁、镍、钾、钠和锶。稀土金属的萃取、分离。萃取生物碱。农药分析。有机合成。抗冻液组分。', '2018-04-13');
INSERT INTO `chemical` VALUES ('91', '甲苯', '', '1', '', '1003001001B001022204', '2-2-2-4', '莫成昊', '2018-04-13', '是', '试剂', '是', '18.50', 'C7H8', '92.14', '108-88-3', '', '无色澄清液体。有苯样气味。有强折光性。能与乙醇、乙醚、丙酮、氯仿、二硫化碳和冰乙酸混溶，极微溶于水。相对密度(d204)0.866。凝固点-95℃。沸点110.6℃。折光率(n20D)1.4967。闪点(闭杯)4.4℃。易燃。蒸气能与空气形成爆炸性混合物，爆炸极限1.2%～7.0％(体积)。低毒，半数致死量(大鼠，经口)5000mg/kg。高浓度气体有麻醉性。有刺激性。', '密封阴凉保存。SCRC100228801308', '溶剂。萃取剂。电子工业用洗涤剂。温度计校正的标准。制造染料、香料、苯甲醛、苯甲酸和其他有机化合物。', '2018-04-13');
INSERT INTO `chemical` VALUES ('92', '正戊酸', '', '1', '', '1003001001B001022205', '2-2-2-5', '莫成昊', '2018-04-13', '否', '试剂', '是', '101.00', 'C5H10O2', '102.13', '109-52-4', '', '无色液体。有不愉快气味。溶于30份水，易溶于乙醇和乙醚。相对密度(d204)0.939。熔点-34.5℃。沸点186～187℃。折光率(n20D)1.4086。闪点88℃。有毒，半数致死量(小鼠，静脉)1290±53mg/kg。有腐蚀性。', '密封保存。SCRC301914400627', '有机合成。', '2018-04-13');
INSERT INTO `chemical` VALUES ('93', '碱性螯合剂', '', '1', '', '1003001001B001022206', '2-2-2-6', '莫成昊', '2018-04-13', '否', '试剂', '是', null, '', '', '', '', '', '', '', '2018-04-13');
INSERT INTO `chemical` VALUES ('94', '过氧化氢', '', '1', '', '1003001001B001022207', '2-2-2-7', '莫成昊', '2018-04-13', '是', '试剂', '是', '12.80', 'H2O2', '34.01', '7722-84-1', '', '无色透明液体，味苦，能缓慢分解成氧和水，少量不纯物质存在时剧烈分解，碱性及金属细粉能加速其分解，亦能被多数有机溶剂分解，可加入少量N-乙酰苯胺和N-乙酰乙氧基苯胺作稳定剂。能与水混溶，溶于乙醚，不溶于石油醚。水溶液呈弱酸性。有氧化性和腐蚀性，对皮肤有化学浸蚀性，能与水混合。密度：1.11熔点：-0.43℃沸点：152℃折光率：1.3350', '用瓶口有微孔的塑料瓶装阴凉保存。SCRC100112800709', '氧化剂。漂白剂。点滴分析测定钙、钴、铜、锰、钛、钒、铵及铬酸。有机合成。', '2018-04-13');
